import React from 'react';
import classNames from 'classnames';
import request from 'request-promise';

import StellarAddressInputStep from './StellarAddressInputStep.jsx'
import './fonts.css';
import './Button.css';

class Button extends React.Component {
    constructor(props) {
        super(props);

        if (!Number.isInteger(this.props.payment.amount) && this.props.payment.amount > 0) {
            throw new Error("payment.amount must be a whole number > 0");
        }

        this.state = {
            step: "browsing",
            ...(this.props.payment)
        }
    }

    onClick() {
        if (this.state.step === "browsing") {
            this.setState({step: "entering"});
        }
    }

    onChangeEntering(event) {
        if (this.state.step === "entering") {
            console.log(event.target.value)
        }
    }

    renderButton() {
        return (
            <div className="warp-button">
                <span className="warp-amount">Pay <strong>{this.props.payment.amount} XLM</strong></span>
                <span className="warp-stellar-logo">
                    <img className="warp-stellar-logo-img" src={
                        "data:image/png;base64,"
                    }/>
                    <span className="warp-stellar-logo-name">
                        <span style={{color: "#08b5e5"}}>stellar</span>
                        <span style={{color: "#e74c3c", fontStyle: "italic"}}>warp</span>
                    </span>
                </span>
            </div>
        );
    }

    renderInput() {
        if (this.state.step !== "entering") return;
        return (
            <StellarAddressInputStep />
        );
    }
    render() {
        console.log(this.state.step);
        return (
            <div className="warp-container" onClick={this.onClick.bind(this)}>
                {this.renderButton()}
                {this.renderInput()}
            </div>
        );
        // switch(this.state.step) {
        //     case "browsing":
        //         return (<div>Hello World</div>);
        //     case "entering":
        //         return (<div>Hello World</div>);
        //     case "signing":
        //     case "confirmed":
        //     case "failed":
        // }
    }
}

export default Button;